import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-student',
  templateUrl: './student.component.html',
  styleUrls: ['./student.component.scss']
})
export class StudentComponent {

  StudentArray : any[] =[];
  name: string = "";
  address: string = "";
  mobile: Number = 0;

  currentStudentID = "";

  constructor(private http: HttpClient){
    this.getAllStudent();
  }

  register(){
    let bodyData = {
      "name" : this.name,
      "address" : this.address,
      "mobile" : this.mobile
    }

    this.http.post("http://localhost:8089/api/v1/student/save", bodyData, {responseType: "text"}).subscribe((resultData: any)=>{
    console.log(resultData);
    alert("Student Registered Succesfully!!");
    // this.getAllCustomer();

    this.name = "";
    this.address = "";
    this.mobile = 0;
    }
  );}

  getAllStudent(){
    this.http.get("http://localhost:8089/api/v1/student/getAll").subscribe((resultData: any)=>{
      console.log("resultData: ", resultData);
      this.StudentArray = resultData;
    });
  }

  setDelete(data: any) {
    this.http.delete("http://localhost:8089/api/v1/student/delete/" + data.id , {responseType: 'text'}).subscribe((resultData: any)=>{
      console.log("Delete Record: ", resultData);
      alert("Student Deleted");
      this.getAllStudent();
      this.name = "";
      this.address = "";
      this.mobile = 0;
    });
  }

  setUpdate(data: any) {
    this.name = data.name;
    this.address = data.address;
    this.mobile = data.mobile;
    this.currentStudentID = data.id;
  }

  updateRecords(){
    let bodyData = {
      "name" : this.name,
      "address" : this.address,
      "mobile" : this.mobile
    };

    this.http.put("http://localhost:8089/api/v1/student/edit/" + this.currentStudentID, bodyData, {responseType: 'text'}).subscribe((resultData: any)=>{
      console.log("Update Record: ", resultData);
      alert("Student Data Updated!");
      this.getAllStudent();

      this.name = '';
      this.address = '';
      this.mobile = 0;
    });
  }

  save(){
    if(this.currentStudentID == ''){
      this.register();
    }
    else{
      this.updateRecords();
    }
  }

}
<div class = "container mt -4">
    <div class="card">
        <h1>Student Registration</h1>
        <form>
            <div class="form-group">
              <label>student name</label>
              <input type="text" [(ngModel)]="name" [ngModelOptions]="{standalone: true}"  class="form-control" id="studentName" name="studentName" placeholder="Enter name">    
            </div>

            <div class="form-group">
                <label>student Address<address></address><Address></Address></label>
                <input type="text" [(ngModel)]="address" [ngModelOptions]="{standalone: true}" class="form-control" id="studentAddress" name="studentAddress" placeholder="Enter Address">    
              </div>

              <div class="form-group">
                <label>student Mobile</label>
                <input type="text" [(ngModel)]="mobile" [ngModelOptions]="{standalone: true}" class="form-control" id="studentMobile" name="studentMobile" placeholder="Enter Mobile">    
              </div>

            <button type="submit"  class="btn btn-primary" (click)="save()">Submit</button>
          </form>
    </div>
</div>

<div class="container mt-4" >
    <h1>Student Table</h1>
    <table class="table">
        <thead>     
            <tr>
                <th scope="col">Name</th>
                <th scope="col">Address</th>
                <th scope="col">Mobile</th>
                <th scope="col">Option</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let StudentItem of StudentArray">
                <td>{{StudentItem.name}}</td>
                <td>{{StudentItem.address }}</td>
                <td>{{StudentItem.mobile }}</td>
                <td>
                    <button type="button" class="btn btn-success" (click)="setUpdate(StudentItem)">Edit</button>
                    <button type="button" class="btn btn-danger" (click)="setDelete(StudentItem)">Delete</button>
                </td>
            </tr>
        </tbody>
    </table>
</div>
