import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-student',
  templateUrl: './student.component.html',
  styleUrls: ['./student.component.scss']
})
export class StudentComponent {
  StudentArray : any[] =[];
  name: string = "";
  address: string = "";
  mobile: Number = 0;

  currentStudentID = "";

  constructor(private http: HttpClient){
    // this.getAll
  }

  register(){
    let bodyData = {
      "name" : this.name,
      "address" : this.address,
      "mobile" : this.mobile
    }

    this.http.post("http://localhost:8089/api/v1/student/save", bodyData, {responseType: "text"}).subscribe((resultData: any)=>{
    console.log(resultData);
    alert("Student Registered Succesfully!!");
    // this.getAllCustomer();

    this.name = "";
    this.address = "";
    this.mobile = 0;
    }
  };


}
<div class = "container mt -4">
    <div class="card">
        <h1>Student Registration</h1>
        <form>
            <div class="form-group">
              <label>student name</label>
              <input type="text" [(ngModel)]="name" [ngModelOptions]="{standalone: true}"  class="form-control" id="studentName" name="studentName" placeholder="Enter name">    
            </div>

            <div class="form-group">
                <label>student Address<address></address><Address></Address></label>
                <input type="text" [(ngModel)]="address" [ngModelOptions]="{standalone: true}" class="form-control" id="studentAddress" name="studentAddress" placeholder="Enter Address">    
              </div>

              <div class="form-group">
                <label>student Mobile</label>
                <input type="text" [(ngModel)]="mobile" [ngModelOptions]="{standalone: true}" class="form-control" id="studentMobile" name="studentMobile" placeholder="Enter Mobile">    
              </div>

            <button type="submit"  class="btn btn-primary" (click)="register()">Submit</button>
          </form>
    </div>
</div>
